import{_ as s,c as e,o as n,ae as i}from"./chunks/framework.DI6M9D4h.js";const u=JSON.parse('{"title":"IP地址基础","description":"了解IP地址的基本概念和组成，网络学习的起点","frontmatter":{"title":"IP地址基础","date":"2026-01-12T00:00:00.000Z","description":"了解IP地址的基本概念和组成，网络学习的起点"},"headers":[],"relativePath":"article/network/1.IP地址基础.md","filePath":"article/network/1.IP地址基础.md"}'),l={name:"article/network/1.IP地址基础.md"};function r(t,a,o,p,d,c){return n(),e("div",null,[...a[0]||(a[0]=[i(`<h1 id="ip地址基础" tabindex="-1">IP地址基础 <a class="header-anchor" href="#ip地址基础" aria-label="Permalink to &quot;IP地址基础&quot;">​</a></h1><h2 id="一、什么是ip地址" tabindex="-1">一、什么是IP地址 <a class="header-anchor" href="#一、什么是ip地址" aria-label="Permalink to &quot;一、什么是IP地址&quot;">​</a></h2><p>IP地址（Internet Protocol Address）是互联网协议地址的简称，用于在网络中唯一标识一台设备。就像现实生活中的门牌号一样，IP地址让网络中的设备能够找到彼此并进行通信。</p><h2 id="二、ip地址的版本" tabindex="-1">二、IP地址的版本 <a class="header-anchor" href="#二、ip地址的版本" aria-label="Permalink to &quot;二、IP地址的版本&quot;">​</a></h2><p>IP地址目前主要有两个版本：</p><h3 id="_2-1-ipv4" tabindex="-1">2.1 IPv4 <a class="header-anchor" href="#_2-1-ipv4" aria-label="Permalink to &quot;2.1 IPv4&quot;">​</a></h3><ul><li><strong>位数</strong>：32位</li><li><strong>表示方式</strong>：点分十进制，如 <code>192.168.1.1</code></li><li><strong>地址数量</strong>：约42亿个（2^32）</li><li><strong>现状</strong>：目前广泛使用，但地址已基本耗尽</li><li><strong>示例</strong>：<code>192.168.1.1</code>、<code>10.0.0.1</code>、<code>172.16.0.1</code></li></ul><h3 id="_2-2-ipv6" tabindex="-1">2.2 IPv6 <a class="header-anchor" href="#_2-2-ipv6" aria-label="Permalink to &quot;2.2 IPv6&quot;">​</a></h3><ul><li><strong>位数</strong>：128位</li><li><strong>表示方式</strong>：冒号分隔的十六进制，如 <code>2001:0db8:85a3:0000:0000:8a2e:0370:7334</code></li><li><strong>地址数量</strong>：几乎无限（2^128）</li><li><strong>现状</strong>：逐步推广中，是未来的趋势</li><li><strong>示例</strong>：<code>2001:0db8::1</code>、<code>fe80::1</code></li></ul><p><strong>注意</strong>：本文主要讨论IPv4地址，因为它是目前最常用的版本。</p><h2 id="三、ipv4地址的组成" tabindex="-1">三、IPv4地址的组成 <a class="header-anchor" href="#三、ipv4地址的组成" aria-label="Permalink to &quot;三、IPv4地址的组成&quot;">​</a></h2><h3 id="_3-1-基本结构" tabindex="-1">3.1 基本结构 <a class="header-anchor" href="#_3-1-基本结构" aria-label="Permalink to &quot;3.1 基本结构&quot;">​</a></h3><p>一个IPv4地址由32位二进制数组成，通常分为4段，每段8位（0-255），用点号分隔：</p><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>192.168.1.1</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h3 id="_3-2-二进制表示" tabindex="-1">3.2 二进制表示 <a class="header-anchor" href="#_3-2-二进制表示" aria-label="Permalink to &quot;3.2 二进制表示&quot;">​</a></h3><p>每个IPv4地址都可以转换为二进制：</p><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>十进制：192.168.1.1</span></span>
<span class="line"><span>二进制：11000000.10101000.00000001.00000001</span></span>
<span class="line"><span>        └───┬───┘└───┬───┘└───┬───┘└───┬───┘</span></span>
<span class="line"><span>          192      168        1        1</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div><h3 id="_3-3-地址范围" tabindex="-1">3.3 地址范围 <a class="header-anchor" href="#_3-3-地址范围" aria-label="Permalink to &quot;3.3 地址范围&quot;">​</a></h3><ul><li><strong>最小值</strong>：<code>0.0.0.0</code>（二进制：00000000.00000000.00000000.00000000）</li><li><strong>最大值</strong>：<code>255.255.255.255</code>（二进制：11111111.11111111.11111111.11111111）</li><li><strong>总数量</strong>：2^32 = 4,294,967,296 个地址</li></ul><h3 id="_3-4-地址格式" tabindex="-1">3.4 地址格式 <a class="header-anchor" href="#_3-4-地址格式" aria-label="Permalink to &quot;3.4 地址格式&quot;">​</a></h3><p>IPv4地址的格式规则：</p><ul><li>由4个数字组成，用点号（.）分隔</li><li>每个数字范围：0-255</li><li>不能有前导零（如 <code>192.168.001.001</code> 是错误的）</li></ul><p><strong>正确示例</strong>：</p><ul><li>✅ <code>192.168.1.1</code></li><li>✅ <code>10.0.0.1</code></li><li>✅ <code>172.16.0.1</code></li></ul><p><strong>错误示例</strong>：</p><ul><li>❌ <code>192.168.001.001</code>（不能有前导零）</li><li>❌ <code>256.1.1.1</code>（超过255）</li><li>❌ <code>192.168.1</code>（缺少一段）</li></ul><h2 id="四、ip地址的作用" tabindex="-1">四、IP地址的作用 <a class="header-anchor" href="#四、ip地址的作用" aria-label="Permalink to &quot;四、IP地址的作用&quot;">​</a></h2><h3 id="_4-1-设备标识" tabindex="-1">4.1 设备标识 <a class="header-anchor" href="#_4-1-设备标识" aria-label="Permalink to &quot;4.1 设备标识&quot;">​</a></h3><p>IP地址就像设备的&quot;身份证&quot;，在网络中唯一标识一台设备：</p><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>设备A：192.168.1.100</span></span>
<span class="line"><span>设备B：192.168.1.101</span></span>
<span class="line"><span>设备C：192.168.1.102</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br></div></div><h3 id="_4-2-网络通信" tabindex="-1">4.2 网络通信 <a class="header-anchor" href="#_4-2-网络通信" aria-label="Permalink to &quot;4.2 网络通信&quot;">​</a></h3><p>设备通过IP地址进行通信：</p><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>设备A（192.168.1.100） → 发送数据 → 设备B（192.168.1.101）</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h3 id="_4-3-路由选择" tabindex="-1">4.3 路由选择 <a class="header-anchor" href="#_4-3-路由选择" aria-label="Permalink to &quot;4.3 路由选择&quot;">​</a></h3><p>路由器根据IP地址决定数据包的转发路径：</p><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>数据包：源IP（192.168.1.100） → 目标IP（10.0.0.1）</span></span>
<span class="line"><span>路由器：根据目标IP地址选择转发路径</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><h2 id="五、常见ip地址示例" tabindex="-1">五、常见IP地址示例 <a class="header-anchor" href="#五、常见ip地址示例" aria-label="Permalink to &quot;五、常见IP地址示例&quot;">​</a></h2><h3 id="_5-1-家庭网络" tabindex="-1">5.1 家庭网络 <a class="header-anchor" href="#_5-1-家庭网络" aria-label="Permalink to &quot;5.1 家庭网络&quot;">​</a></h3><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>路由器：192.168.1.1</span></span>
<span class="line"><span>电脑：192.168.1.100</span></span>
<span class="line"><span>手机：192.168.1.101</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br></div></div><h3 id="_5-2-企业网络" tabindex="-1">5.2 企业网络 <a class="header-anchor" href="#_5-2-企业网络" aria-label="Permalink to &quot;5.2 企业网络&quot;">​</a></h3><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>服务器：10.0.0.10</span></span>
<span class="line"><span>办公电脑：10.0.0.100</span></span>
<span class="line"><span>打印机：10.0.0.200</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br></div></div><h3 id="_5-3-特殊地址" tabindex="-1">5.3 特殊地址 <a class="header-anchor" href="#_5-3-特殊地址" aria-label="Permalink to &quot;5.3 特殊地址&quot;">​</a></h3><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>本地回环：127.0.0.1（localhost）</span></span>
<span class="line"><span>广播地址：255.255.255.255</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><h2 id="六、学习路径" tabindex="-1">六、学习路径 <a class="header-anchor" href="#六、学习路径" aria-label="Permalink to &quot;六、学习路径&quot;">​</a></h2><p>了解IP地址基础后，可以继续学习：</p><ol><li><strong>局域网和公网IP</strong> - 了解IP地址的分类和用途</li><li><strong>特殊IP地址</strong> - 了解链路本地地址和回环地址</li><li><strong>子网掩码和CIDR</strong> - 深入学习网络划分</li><li><strong>DHCP动态主机配置</strong> - 了解IP地址的自动分配</li><li><strong>网络配置实践</strong> - 学习如何配置和使用IP地址</li></ol><h2 id="七、总结" tabindex="-1">七、总结 <a class="header-anchor" href="#七、总结" aria-label="Permalink to &quot;七、总结&quot;">​</a></h2><ul><li>IP地址是网络中设备的唯一标识</li><li>IPv4是目前最常用的版本（32位）</li><li>IPv6是未来的趋势（128位）</li><li>IP地址由4个0-255的数字组成</li><li>理解IP地址是学习网络的基础</li></ul><p>掌握IP地址的基础知识后，你就可以继续深入学习网络的其他概念了！</p>`,49)])])}const b=s(l,[["render",r]]);export{u as __pageData,b as default};
